mapper.insert:

	SELECT #{spell}||LPAD((NVL((MAX(TO_NUMBER(SUBSTR(cust_no, -2)))),0) + 1), 2, '0')
	FROM   cust	
	WHERE  cust_no LIKE '${spell}%'	

INSERT INTO cust (
                     cust_no
                    ,brn
                    ,cust_name
                    ,rep
                    ,cust_addr
                    ,cust_tel
                    ,pay_pic
                    ,pay_pic_tel
                  )
  VALUES (
             #{custNo}
            ,#{brn}
            ,#{custName}
            ,#{rep}
            ,#{custAddr}
            ,#{custTel}
            ,#{payPic}
            ,#{payPicTel}
         )

mapper.select:
SELECT g.rn ,g.cust_no ,g.cust_name ,g.rep ,g.pay_pic_tel ,g.last_ct_stat ,g.last_ct_stat_name ,g.last_ct_no FROM ( SELECT ROW_NUMBER() OVER (ORDER BY ${sch.sortCondition} ) rn ,c.cust_no ,c.cust_name ,c.rep ,c.pay_pic_tel ,l.ct_stat as last_ct_stat ,NVL2(l.ct_stat, get_codename(l.ct_stat), '미계약') as last_ct_stat_name ,l.ct_no as last_ct_no FROM cust c LEFT OUTER JOIN ( SELECT ROW_NUMBER() OVER (PARTITION BY cust_no ORDER BY ct_end_dt DESC) crn ,cust_no, ct_no, ct_stat FROM ct ) l ON (c.cust_no = l.cust_no and l.crn = 1) WHERE c.cust_no <> '0000'
AND ${sch.keywordCondition} LIKE '%${sch.keyword}%'
AND l.ct_stat ${sch.searchCondition}
) g WHERE g.rn <= (#{sch.page} * 10) AND g.rn > (#{sch.page} - 1) * 10

SELECT COUNT(*) FROM ( SELECT ROW_NUMBER() OVER (ORDER BY c.cust_no) rn ,c.cust_no ,c.cust_name ,c.rep ,l.ct_stat as last_ct_stat ,l.ct_no as last_ct_no FROM cust c LEFT OUTER JOIN ( SELECT ROW_NUMBER() OVER (PARTITION BY cust_no ORDER BY ct_end_dt DESC) crn ,cust_no, ct_no, ct_stat FROM ct ) l ON (c.cust_no = l.cust_no and l.crn = 1) WHERE c.cust_no <> '0000'
AND ${sch.keywordCondition} LIKE '%${sch.keyword}%'
AND l.ct_stat ${sch.searchCondition}
) g
