mapper.select:
SELECT g.* FROM (SELECT rownum rn, t.* FROM (SELECT r.rec_email_no, r.recp, r.refer, r.cust_no, r.recp_type, r.email_stat AS rstat, s.email_stat AS sstat, s.sen_email_no, s.title, s.sender, s.sent_dt, id_to_the_name(sender) AS sender_name, id_to_the_name(recp) AS recp_name, id_to_the_name(refer) AS refer_name FROM email s JOIN inbox r ON (s.sen_email_no = r.sen_email_no) WHERE r.email_stat = '3B1b' AND r.cust_no = #{custNo, jdbcType=VARCHAR} AND (r.recp = #{recp} OR r.refer = #{recp})
AND s.title LIKE '%' || #{keyword} || '%'
AND s.cntn LIKE '%' || #{keyword} || '%'
AND id_to_the_name(s.sender) LIKE '%' || #{keyword} || '%'
AND r.recp_Type LIKE #{recpType}
AND s.sent_Dt >= #{sdStart}
AND s.sent_Dt <= #{sdEnd}
ORDER BY ${emSort} ) t WHERE rownum <= (#{page} * 10)) g WHERE g.rn > (#{page} -1) * 10

        SELECT COUNT(*)
        FROM inbox r JOIN email s
                     ON (s.sen_email_no = r.sen_email_no)
		WHERE  r.email_stat = '3B1b'  
		AND  r.cust_no = #{custNo, jdbcType=VARCHAR}
		AND  (r.recp = #{recp} OR r.refer = #{recp})

			AND ${searchCondition} LIKE '%' || #{keyword} || '%'


			AND r.recp_Type LIKE #{recpType}


			AND s.sent_Dt >= #{sdStart}


			AND s.sent_Dt <= #{sdEnd}

mapper.insert:

	SELECT NVL(TO_NUMBER(MAX(file_no)), 0) + 1
	FROM email_file

INSERT ALL

	INTO email_file
	        (
	            file_no
	            ,sen_email_no
	            ,save_name
	            ,upl_name
	            ,file_ext
	            ,file_dt
	            ,file_size
	            ,upl_emp      
	        )
	  VALUES
	        (
	         #{fno} + #{no}
	        ,#{file.senEmailNo}
	        ,#{file.saveName}
	        ,#{file.uplName}
	        ,#{file.fileExt}
	        ,SYSDATE
	        ,#{file.fileSize}
	        ,#{file.uplEmp}
	        )	

