mapper.select:
SELECT g.* FROM (SELECT rownum rn, t.* FROM (SELECT s.sen_email_no, r.recp, r.refer, r.cust_no, r.recp_type, r.email_stat AS rstat, s.email_stat AS sstat, s.title, s.sender, s.sent_dt, id_to_the_name(sender) AS sender_name, id_to_the_name(recp) AS recp_name, id_to_the_name(refer) AS refer_name FROM email s JOIN inbox r ON (s.sen_email_no = r.sen_email_no) WHERE s.email_stat = '3B1b' AND r.cust_no = #{custNo, jdbcType=VARCHAR} AND s.sender = #{sender}
AND s.title LIKE '%' || #{keyword} || '%'
AND s.cntn LIKE '%' || #{keyword} || '%'
AND id_to_the_name(r.recp) LIKE '%' || #{keyword} || '%'
AND s.sent_Dt >= #{sdStart}
AND s.sent_Dt <= #{sdEnd}
ORDER BY ${emSort} ) t WHERE rownum <= (#{page} * 10)) g WHERE g.rn > (#{page} -1) * 10

        SELECT COUNT(*)
        FROM inbox r JOIN email s
                     ON (s.sen_email_no = r.sen_email_no)
		WHERE	sender = #{sender}
		AND     s.cust_no = #{custNo}
		AND     s.email_stat = '3B1b'	

			AND ${searchCondition} LIKE '%' || #{keyword} || '%'


			AND s.sent_Dt >= #{sdStart}


			AND s.sent_Dt <= #{sdEnd}

SELECT g.* FROM (SELECT rownum rn, t.* FROM (SELECT s.sen_email_no, r.rec_email_no, r.recp, r.refer, r.cust_no, r.recp_type, r.email_stat AS rstat, s.email_stat AS sstat, s.title, s.sender, s.sent_dt FROM email s JOIN inbox r ON (s.sen_email_no = r.sen_email_no) WHERE r.cust_no = #{custNo, jdbcType=VARCHAR} AND (r.email_stat = '3B2b' OR s.email_stat = '3B2b') AND (s.sender = #{sender, jdbcType=VARCHAR} OR r.recp = #{recp, jdbcType=VARCHAR} OR r.refer = #{refer, jdbcType=VARCHAR})
AND ${searchCondition} LIKE '%' || #{keyword} || '%'
AND s.sent_Dt >= #{sdStart}
AND s.sent_Dt <= #{sdEnd}
ORDER BY ${emSort} ) t WHERE rownum <= (#{page} * 10)) g WHERE g.rn > (#{page} -1) * 10

        SELECT COUNT(*)
        FROM inbox r JOIN email s
                     ON (s.sen_email_no = r.sen_email_no)
                    WHERE  r.cust_no = #{custNo, jdbcType=VARCHAR} 
                    AND    (r.email_stat = '3B2b' OR s.email_stat = '3B2b')
                    AND  (s.sender = #{sender, jdbcType=VARCHAR}
                    OR    r.recp = #{recp, jdbcType=VARCHAR} 
                    OR    r.refer = #{refer, jdbcType=VARCHAR})	

			AND ${searchCondition} LIKE '%' || #{keyword} || '%'


			AND s.sent_Dt >= #{sdStart}


			AND s.sent_Dt <= #{sdEnd}
