mapper.select:
SELECT g.rn ,g.emp_id ,g.perm_id ,g.perm_name ,g.dept_id ,g.dept_name ,g.emp_name ,g.job_no ,g.job_title ,g.hire_dt ,g.emp_stat ,g.emp_stat_name FROM ( SELECT ROW_NUMBER() OVER (ORDER BY e.emp_id) rn ,e.emp_id ,j.perm_id ,p.perm_name ,e.dept_id ,d.dept_name ,e.emp_name ,e.job_no ,j.job_title ,e.hire_dt ,e.emp_stat ,get_codename(emp_stat) as emp_stat_name FROM emp e JOIN dept d ON (e.dept_id = d.dept_id) JOIN jobs j ON (e.job_no = j.job_no) JOIN perm p ON (j.perm_id = p.perm_id) WHERE cust_no <> '0000' AND cust_no = #{cno}
AND emp_stat = '1D1d' and substr(emp_id, -3) = '000'
AND e.emp_stat = #{sch.searchCondition}
AND j.perm_id = #{sch.keywordCondition}
) g WHERE g.rn <= (#{sch.page} * 10) AND g.rn > (#{sch.page} - 1) * 10

SELECT COUNT(*) FROM ( SELECT e.emp_id ,j.perm_id ,e.emp_stat FROM emp e JOIN dept d ON (e.dept_id = d.dept_id) JOIN jobs j ON (e.job_no = j.job_no) JOIN perm p ON (j.perm_id = p.perm_id) WHERE cust_no <> '0000' AND cust_no = #{cno}
AND emp_stat = '1D1d' and substr(emp_id, -3) = '000'
AND e.emp_stat = #{sch.searchCondition}
AND j.perm_id = #{sch.keywordCondition}
)

SELECT COUNT(*)
FROM emp
WHERE cust_no = #{cno}
AND  emp_no = #{eno}

mapper.insert:

	SELECT #{custNo}||#{empNo}
	FROM   dual

INSERT INTO emp(
                    emp_id,
                    ct_no,
                    cust_no,
                    dept_id,
                    job_no,
                    emp_no,
                    pw,
                    emp_name,
                    hire_dt,
                    emp_stat,
                    emp_addr1,
                    emp_addr2,
                    emp_addr3,
                    emp_addr4,
                    emp_tel,
                    email_addr
                )
    VALUES (
                #{empId}
                ,#{ctNo}
                ,#{custNo}
                ,#{deptId}
                ,#{jobNo}
                ,#{empNo}
                ,#{pw}
                ,#{empName}
                ,#{hireDt}
                ,'1D1d'
                ,#{empAddr1}
                ,#{empAddr2}
                ,#{empAddr3}
                ,#{empAddr4}
                ,#{empTel}
                ,#{custNo}||#{empNo}||'@'||#{custNo}||'.yedam'
            )

mapper.update:
UPDATE  emp
SET		emp_no = emp_no

			, pw = #{emp.pw}	


        	, emp_name = #{emp.empName}


       		, dept_id = #{emp.deptId}


	        , job_no = #{emp.jobNo}


       		, emp_tel = #{emp.empTel}


       		, hire_dt = #{emp.hireDt}


       		, fire_dt = #{emp.fireDt}


       		, emp_addr1 = #{emp.empAddr1}
       		, emp_addr2 = #{emp.empAddr2}
       		, emp_addr3 = #{emp.empAddr3}
       		, emp_addr4 = #{emp.empAddr4}

WHERE  emp_id = #{emp.empId}
