mapper.sql:
WHERE p.cust_no NOT IN '0000'

	AND ${searchCondition} LIKE '%' || #{keyword} || '%'


	AND p.pay_stat LIKE #{payStat}


	AND ${payDt} >= #{payStart}


	AND ${payDt} <= #{payEnd}

mapper.update:
UPDATE pay
SET serv_amt = #{serv_amt}
WHERE  ct_no = #{ctNo}

UPDATE pay
SET pay_stat = '0D2d'
WHERE ct_no = #{ctNo}


		AND merchant_uid = #{merchantUid}


		AND merchant_uid IS NULL
