mapper.select:
SELECT r.* FROM (SELECT rownum rn, t.* FROM (SELECT prjt_id ,prjt_name ,prjt_start_dt ,prjt_due_dt ,purpose ,smry ,get_codename(prjt_stat) as prjt_stat FROM prjt
AND ${searchCondition} LIKE '%' || #{keyword} || '%'
AND prjt_stat LIKE #{prjtStat}
AND prjt_start_dt >= #{prjtStart} AND prjt_due_dt <= #{prjtDue}
AND prjt_start_dt >= #{prjtStart}
AND prjt_due_dt <= #{prjtDue}
ORDER BY ${prjtSort} DESC, prjt_id DESC) t WHERE rownum <= (#{page} * 10)) r WHERE r.rn > (#{page} -1) * 10

SELECT COUNT(*)
FROM prjt 



				AND ${searchCondition} LIKE '%' || #{keyword} || '%'


				AND prjt_stat LIKE #{prjtStat}



				AND prjt_start_dt >= #{prjtStart}  AND prjt_due_dt <= #{prjtDue}


				AND prjt_start_dt >= #{prjtStart}


				AND prjt_due_dt <= #{prjtDue}

mapper.insert:


	SELECT 'CPJ'||LPAD(NVL((MAX(TO_NUMBER(SUBSTR(prjt_id, -4)))),0) + 1, 4, '0') 
	FROM prjt

INSERT INTO prjt (prjt_id 
					,prjt_name 
					,prjt_stat 
					,prjt_start_dt 
					,prjt_due_dt 
					,smry 
					,purpose 
					,resp_mngr_id
					,cust_no)
			VALUES (#{prjtId}
					,#{prjtName}
					,#{prjtStat}
					,#{prjtStartDt}
					,#{prjtDueDt}
					,#{smry}
					,#{purpose}
					,#{respMngrId}
					,#{custNo})

mapper.update:
UPDATE prjt
SET   

		 prjt_name = #{prjtName}


		, prjt_stat =  #{prjtStat}


		, prjt_start_dt = #{prjtStartDt}


		, prjt_due_dt = #{prjtDueDt}


		, purpose = #{purpose}


		, smry = #{smry}


		, resp_mngr_id = #{respMngrId}

WHERE prjt_id = #{prjtId}
AND cust_no =#{custNo}

mapper.select:
SELECT  tc.task_no
       ,tc.dept_id
       ,get_deptname(tc.dept_id) as dept_name 
       ,tc.task_name
FROM   task_common tc
WHERE  task_due_dt > SYSDATE
AND	


			tc.cust_no = #{emp.custNo}


			tc.cust_no = #{emp.custNo}
			AND (   
					tc.dept_id = #{emp.deptId}
			     or tc.task_no IN (SELECT task_no FROM task_emps WHERE task_emp_id = #{emp.empId})
				)


			tc.cust_no = #{emp.custNo}
			AND tc.task_no IN (SELECT task_no FROM task_emps WHERE task_emp_id = #{emp.empId})
