mapper.select:
SELECT r.* FROM (SELECT rownum rn, t.* FROM (SELECT t.task_no, p.prjt_name, t.task_name, t.task_start_dt, t.task_due_dt, get_codename(t.task_type) as task_type, get_codename(t.prjt_mat) as prjt_mat, (SELECT COUNT(*) FROM task_emps te WHERE te.task_no = t.task_no) AS participants_cnt, (SELECT CASE WHEN COUNT(*) = 0 THEN 0 ELSE TRUNC(SUM(CASE WHEN te.cmplt= '5F1f' THEN 1 ELSE 0 END) * 100.0 /COUNT(*)) END FROM task_emps te WHERE te.task_no = t.task_no) AS progress FROM task_common t JOIN prjt p ON (p.prjt_id = t.standard_no)
AND ${searchCondition} LIKE '%' || #{keyword} || '%'
AND t.task_type LIKE #{taskType}
AND t.prjt_mat LIKE #{prjtMat}
AND t.task_start_dt >= #{taskStart} AND t.task_due_dt <= #{taskDue}
AND t.task_start_dt >= #{taskStart}
AND t.task_due_dt <= #{taskDue}
ORDER BY ${taskSort} DESC ,task_no DESC) t WHERE rownum <= (#{page} * 10)) r WHERE r.rn > (#{page} -1) * 10

SELECT COUNT(*)
FROM task_common t JOIN prjt p
					 ON (p.prjt_id = t.standard_no)



		AND ${searchCondition} LIKE '%' || #{keyword} || '%'


		AND t.task_type LIKE #{taskType}


		AND t.prjt_mat LIKE #{prjtMat}



			AND t.task_start_dt >= #{taskStart}  AND t.task_due_dt <= #{taskDue}


			AND t.task_start_dt >= #{taskStart}


			AND t.task_due_dt <= #{taskDue}

mapper.update:
UPDATE task_emps
SET cmplt = '5F2f'
WHERE task_no = #{taskNo}

{CALL
	task_ok(
		#{taskNo, mode=IN, jdbcType=INTEGER},
		#{taskEmpId, mode=IN, jdbcType=VARCHAR},
		#{result, mode=OUT, jdbcType=INTEGER}
	)
}

	   UPDATE task_common
       SET prjt_mat = '5B1b'
       WHERE task_no = #{taskNo};

		UPDATE prjt
      	SET prjt_stat = '5E1e'
      	WHERE prjt_id = #{prjtId}

	UPDATE task_common
	SET 

			 task_name = #{taskName},


			 prjt_mat = #{prjtMat},


			 task_start_dt = #{taskStartDt}


			 task_start_dt = #{taskDueDt},


			 task_purpose = #{taskPurpose},


			task_cntn = #{taskCntn}

	WHERE task_no= #{taskNo}
 	AND cust_no =#{custNo}
	AND standard_no=#{standardNo}

mapper.insert:

	SELECT NVL(MAX(coop_co_no),0) +1
	FROM coop_co

INSERT INTO coop_co ( task_no
					,coop_co_no
					,co_name
					,co_tel
					,pic
					,cust_addr
					,cntn)
VALUES ( #{taskNo}
		,#{coopCoNo}
		,#{coName}
		,#{coTel}
		,#{pic}
		,#{custAddr}
		,#{cntn} )
