mapper.select:
SELECT l.* FROM (SELECT t.* FROM(SELECT e.*, rownum rn FROM (SELECT (SELECT dept_name FROM dept WHERE dept_id = emp.dept_id) dept_id , emp_id, emp_no, emp_name, (SELECT job_title FROM jobs WHERE job_no = emp.job_no) job_no , hire_dt, emp_tel FROM emp WHERE cust_no = #{custNo}
AND dept_id = #{deptId}
) e WHERE ROWNUM <= (#{page} * 5)) t) l WHERE l.rn > (#{page} -1) * 5

SELECT count(*) FROM (SELECT (SELECT dept_name FROM dept WHERE dept_id = emp.dept_id) dept_id , emp_id, emp_no, emp_name, (SELECT job_title FROM jobs WHERE job_no = emp.job_no) job_no , hire_dt, emp_tel FROM emp WHERE cust_no = #{custNo}
AND dept_id = #{deptId}
)

mapper.update:
UPDATE wk
SET wk_in = sysdate,
    wk_stat = GET_wkTIME(),
    wk_in_loc = #{wkInLoc}
where emp_id = #{empId} AND dt = #{dt}

mapper.select:
SELECT COUNT(wk_in)
FROM WK
WHERE emp_id = #{empId} AND dt = #{dt}

mapper.update:
UPDATE wk
SET wk_out = sysdate,
    wk_out_loc = #{wkOutLoc}
where emp_id = #{empId} AND dt = #{dt}

UPDATE wk
SET wk_out = sysdate,
    wk_stat = '2B4b',
    wk_out_loc = #{wkOutLoc}
where emp_id = #{empId} AND dt = #{dt}
